    //console.warn('onMoveThatToThis');
    this.game.log(
      "log",
      "high",
      this.name + " onMoveThatToThis " + asset.name + ".",
      "Behavior"
    );
    var results;

    //
    if ("string" !== typeof aspect) {
      aspect = this.default_aspect;
    }

    this.game.debug(
      `F1930 | onMoveThatToThis.js | move ${asset.id} ${aspect} ${this.id} `
    );

